mapper.select:
SELECT IFNULL(count(mno),0)
  FROM liked
 WHERE bno = #{bno}
   AND mno = #{mno};

mapper.insert:
INSERT INTO reservation ( mno
					    , bno
						, rdate
						, ddate
						, totalprice
						, payment
						, stno
	   						) 
       VALUES (   #{mno}
     	   		, #{rbno}
     	   		, now()
     	   		, date_add(now(),INTERVAL #{ddate} day) 
     	   		, #{totalPrice}
     	   		, "카드"
     	   		, DEFAULT
      );

		INSERT INTO rsv_laundry VALUES ((SELECT MAX(rno) 
										      FROM reservation 
										     WHERE mno = #{mno})
										  , #{list.lno}
										  , #{list.cnt} 
										  , 1
										);
